import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

const list = [
    {
        "input_text": "hola",
        "language": "es",
        "output_text": "hello"
    },
    {
        "input_text": "como estas",
        "language": "es",
        "output_text": "how are you"
    },
    {
        "input_text": "bienvenidos",
        "language": "es",
        "output_text": "Welcome"
    }
]

class App extends Component {
  state = {
    translations: []
  };

  async componentDidMount() {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/translations');
      const translations = await res.json();
      this.setState({
        translations
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <table>
        <tr>
            <td>Entered Text</td>
            <td>Language of Input</td>
            <td>English Translation</td>
        </tr>
       {this.state.translations.map(item => (
        <tr>
          <td key={item.input_text}>{item.input_text}</td>
          <td key={item.language}>{item.language}</td>
          <td key={item.output_text}>{item.output_text}</td>
        </tr>
      ))}
      </table>
    );
  }
}

export default App;
