import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class Nav extends Component{
  barToggle() {
    let linksEl = document.querySelector('.narrowLinks');
    if (linksEl.style.display === 'block') {
      linksEl.style.display = 'none';
    } else {
      linksEl.style.display = 'block';
    }
  }

  render() {
    return (
			<nav>
				<div className="navWide">
					<div className="wideDiv">
						<a href="#home">Home</a>
						<a href="#history">History</a>
						<a href="#new">New</a>
					</div>
				</div>
				<div className="navNarrow">
					<i className="fa fa-bars fa-2x" onClick={this.barToggle}></i>
					<div className="narrowLinks">
						<a href="#home" onClick={this.barToggle}>Home</a>
						<a href="#history" onClick={this.barToggle}>History</a>
						<a href="#new" onClick={this.barToggle}>New</a>
					</div>
				</div>
			</nav>
		);
	}
}


class TextForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'Please enter a new phrase.'
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A New Phrase Was Submitted For Translation: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Entered Text:
          <textarea value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

// export default TextForm;


class Table extends Component {
  state = {
    translations: []
  };

  async componentDidMount() {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/translations');
      const translations = await res.json();
      this.setState({
        translations
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <table>
        <tr>
            <td>Entered Text</td>
            <td>Language of Input</td>
            <td>English Translation</td>
        </tr>
       {this.state.translations.map(item => (
        <tr>
          <td key={item.input_text}>{item.input_text}</td>
          <td key={item.language}>{item.language}</td>
          <td key={item.output_text}>{item.output_text}</td>
        </tr>
      ))}
      </table>
    );
  }
}


class HomePage extends Component{
  render() {
    return (
      <div>
        <Nav />
        <TextForm />
      </div>
    );
  }
}

export {Nav, Table, TextForm, HomePage};
